<script>
    export default {
        name: "Scheme",
        props: ['h', 'h0', 'hG', 'v', 'p'],

        methods: {
            tankHeight(h) {
                let currentH = h;

                let baseHeight = 10;
                let baseHeightDrawing = 40.254463;

                let currentHeightDrawing = baseHeightDrawing / baseHeight * currentH;
                return currentHeightDrawing;
            },
            tankY(h, index) {
                let baseHeight = 10;

                let baseHeightDrawing = this.tankHeight(baseHeight);
                let heightDelta = baseHeightDrawing - this.tankHeight(h);

                let baseYs = [40.354164, 100.54688];

                let baseY = baseYs[index];
                return baseY + heightDelta;
            },
            waterHeight(h) {
                let currentH = h;

                let baseHeight = 10;
                let baseHeightDrawing = 39.8;

                let currentHeightDrawing = baseHeightDrawing / baseHeight * currentH;
                return currentHeightDrawing;
            },
            waterY(h, index) {
                let baseHeight = 10;

                let baseHeightDrawing = this.waterHeight(baseHeight);
                let heightDelta = baseHeightDrawing - this.waterHeight(h);

                let baseYs = [40.9, 101.0];

                let baseY = baseYs[index];
                return baseY + heightDelta;
            },

            precision(float) {
                return parseFloat(float).toFixed(1);
            },

            hText(float) {
                return this.precision(float);
            },
            pText(float) {
                return this.precision(float/1000);
            },
            vText(float) {
                return (float*1e6).toFixed(0);
            },

            getFlowStroke(v) {
                let minStroke = 0.25;
                let maxStroke = 5;

                let minV = 0.0001;
                let maxV = 0.01;

                let strokeMultiplier = (maxStroke-minStroke)/(maxV-minV);
                let strokeWidth = strokeMultiplier * Math.abs(v);

                if (strokeWidth > maxStroke) {
                    strokeWidth = maxStroke;
                }

                if (strokeWidth < minStroke) {
                    strokeWidth = minStroke;
                }

                return strokeWidth;
            },

            vStyle(v) {
                let strokeWidth = this.getFlowStroke(v);
                return `fill:none;fill-rule:evenodd;stroke:#006aff;stroke-width:${strokeWidth}px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1`;
            }
        },
        computed: {
            textStyle() {
                return `font-style:normal;font-weight:normal;font-size:2.11666656px;line-height:125%;font-family:Sans;letter-spacing:0px;word-spacing:0px;fill:#000000;fill-opacity:1;stroke:none;stroke-width:0.26458332px;stroke-linecap:butt;stroke-linejoin:miter;stroke-opacity:1`;
            }
        }
    }
